var isTimeout;

//로그인 레이어 팝업 Toggle
function loginLayerToggle(options) {
    var isOpen = $(document).find('ul.top_utils li.util_login').hasClass('on');

    if (options.isOpen && !isOpen) {
    	$(document).find('ul.top_utils li.util_login').addClass('on');
    } else if (!options.isOpen && isOpen) {
    	$(document).find('ul.top_utils li.util_login').removeClass('on');
    }
    
    clearTimeout(isTimeout);
};

//로그인 레이어 팝업 관련 쿠키 설정 - 브라우저 종료 전까지 유지
function setLoginLayerCookie(key, value) {
	var domain_l = window.location.hostname;
	document.cookie = key + "=" + value + "; domain=" + (domain_l.indexOf('uplussave.com') != -1 ? '.uplussave.com' : domain_l) + "; path=/";
}

//로그인 레이어 팝업 관련 쿠키 값 얻어오기
function getLoginLayerCookie(key) {
	var results = document.cookie.match('(^|;) ?' + key + '=([^;]*)(;|$)');
	if (results) {
		return ( unescape(results[2]) );
	} else {
		return undefined;
	}
}

//로그인 레이어 팝업 호출
function loginLayerLoaded(value) {
    var ckVal = getLoginLayerCookie(value);
    
    if (ckVal === undefined) {
    	setLoginLayerCookie(value, true);
        loginLayerToggle({'isOpen': true});
        isTimeout = setTimeout(function () {
        	loginLayerToggle({'isOpen': false});
        }, 3000);
    }
};
