function getVersionIE() {
	var word;
	var version = "N/A";

	var agent = navigator.userAgent.toLowerCase();

	// IE old version
	if ( navigator.appName == "Microsoft Internet Explorer" ) {
		word = "msie ";
	} else {
		// IE 11
		if ( agent.search( "trident" ) > -1 ) word = "trident/.*rv:";

		// Edge
		else if ( agent.search( "edge/" ) > -1 ) word = "edge/";

		// 그외
		else return version;
	}

	var reg = new RegExp( word + "([0-9]{1,})(\\.{0,}[0-9]{0,1})" );

	if (  reg.exec( agent ) != null  ) version = RegExp.$1 + RegExp.$2;

	var checkVersion = function(verNum){
		verNum = parseInt(verNum);

		if ( isNaN(verNum) ) {
			return -1;
		} else {
			return verNum;
		}
	};

	return checkVersion(version);
}

/* ie10, ie11 textarea error */
function textareaError($area) {
	var verNum = getVersionIE();

	if( verNum == 10 || verNum == 11 ) {
		var $textarea = $area.find("textarea[placeholder]");
		var errorIndex = [];

		$textarea.each(function(index) {
			if ( $(this).attr("placeholder") == $(this).val() ) {
				errorIndex.push( $(this) );
			}
		});

		if(errorIndex.length > 0) {
			for(var i=0; i <= errorIndex.length - 1; i++) {
				errorIndex[i].val("");
			}
		}
	}
}

