/*
 * layer popup 추가
 * 1. layer 버튼        : .btn_layer (스타일 요소 아님)
 * 2. layer 닫기 버튼 : .btn_close (스타일 요소 아님)
 * 3. 자주하는 질문 아닐 경우 : .btn_layer[data-layer=#id] 추가 --> 해당 .layer_pop에 id값 추가
 */
$(function(){
	$(document).on("click", ".btn_layer", function(e){
		var $this = $(this),
			// $layer = $this.data("layer") != undefined ? $($this.data("layer")) : $this.closest(".acc_cont").find(".layer_pop"),
			$layer = $this.data("layer") != undefined ? $($this.data("layer")) : $this.closest(".acc_cont").find(".layer_pop"),
			$exec = $layer.attr("data-exec") != undefined ? $layer.attr("data-exec") : '',
			$mask = $("#msk");

		// exec의 값이 존재할 경우 하위 로직 스킵
		if ($exec == "N") {
			return false;
		}

		_layerCenter();

		$mask.show();
		$mask.css('z-index', '100');
		$layer.show().attr("tabindex", "0").focus();

		$(window).on("resize", function(){
			setTimeout(_layerCenter, 0);
		});
		$layer.on("click", ".btn_close, .pop_close", _layerClose);
		// $mask.on("click", _layerClose);

		function _layerCenter() {
			var scrollHeight = $(window).scrollTop(),
				windowHeight = $(window).height(),
				layerHeight = $layer.outerHeight(true),
				_top = windowHeight <= layerHeight ? scrollHeight : scrollHeight + ((windowHeight - layerHeight) / 2);
		    $layer.css("top", _top);
		}
		function _layerClose() {
			$(window).off("resize", _layerCenter);
			$layer.hide();
			$mask.hide();
			$this.focus();
			$mask.css('z-index', '55');

			return false;
		}
		e.preventDefault();
	});
	//190807 [박] 딤드 팝업 닫기 추가
	$('.layer_close').on("click", function() {
		$('.layer_pop').hide();
		$("#msk").hide().removeAttr('style');
	});

});
